  methods: {
    login() {
      // const loading = this.$loading.show()
      this.$store
        .dispatch("login", {
          email: this.email,
          password: this.password,
        })
        .then(
          () => {
            // loading.hide()
            this.$router.push({
              name: "second-page",
            });
          },
          (error) => {
            this.$swal.fire({
              icon: "error",
              title: "Oops...",
              text: "Invalid Email or password !",
            });
            this.message =
              (error.response && error.response.data) ||
              error.message ||
              error.toString();
          }
        );
    },
